import 'package:flutter/material.dart';class ColorContainer extends StatelessWidget {  final Color color;  final String title;  const ColorContainer({Key key, this.color, this.title}) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      padding: EdgeInsets.symmetric(vertical: 20,horizontal: 20),      decoration: BoxDecoration(          borderRadius: BorderRadius.circular(20),          shape: BoxShape.rectangle,          color: color      ),      child: Center(child: Text(title,style: TextStyle(          fontSize: 16,fontWeight: FontWeight.w700,color: Colors.white      ),)),    );  }}