import 'package:flutter/material.dart';class DateWidget extends StatelessWidget {  final Color containerColor, dateColor, dayColor;  final String day;  final int date;  const DateWidget({Key key, this.containerColor, this.dateColor, this.dayColor, this.date, this.day}) : super(key: key);  @override  Widget build(BuildContext context) {    return Card(        elevation: 3,        shape: RoundedRectangleBorder(            borderRadius: BorderRadius.circular(25)        ),        child: Container(          padding: EdgeInsets.symmetric(vertical: 16,horizontal: 10),          decoration: BoxDecoration(            borderRadius: BorderRadius.circular(25),            color: containerColor,          ),          child: Column(            mainAxisAlignment: MainAxisAlignment.spaceEvenly,            children: [              Text(date.toString(),style: TextStyle(                  fontWeight: FontWeight.w700,fontSize: 18,color: dateColor              ),),              SizedBox(height: 5,),              Text(day,style: TextStyle(                  fontWeight: FontWeight.w500,fontSize: 16,color: dayColor              ),overflow: TextOverflow.ellipsis,)            ],          ),        ));  }}