import 'package:flutter/material.dart';class DoctorWidget extends StatelessWidget {  final double height;  final double width;  final AssetImage assetImage;  const DoctorWidget({Key key, this.height, this.width, this.assetImage}) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      height: height,      width: width,      decoration: BoxDecoration(          shape: BoxShape.circle,          color: Colors.white,          image: DecorationImage(            image: assetImage,          )      ),    );  }}