import 'package:flutter/material.dart';class RowWidget extends StatelessWidget {  final Color color;  final IconData icon;  final String title, totalDoctors;  const RowWidget({Key key, this.color, this.title, this.totalDoctors, this.icon}) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      decoration: BoxDecoration(          shape: BoxShape.rectangle,          borderRadius: BorderRadius.circular(15)      ),      padding: EdgeInsets.symmetric(vertical: 5,horizontal: 10),      margin: EdgeInsets.only(left: 20),      height: 50,      width: 130,      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceEvenly,        children: [          Container(            height: 50,            width: 40,            decoration: BoxDecoration(                borderRadius: BorderRadius.circular(15),                color: color            ),            child: Icon(icon,color: Colors.white,),          ),          SizedBox(width: 5,),          Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              Text(title,                style: TextStyle(fontWeight: FontWeight.w500,                    color: Colors.black87,fontSize: 16),),              Text(totalDoctors,                style: TextStyle(fontWeight: FontWeight.w500,                    color: Colors.black45,fontSize: 12),),            ],          )        ],      ),    );  }}